import { EventBus } from '@/main';
import HeaderApp from "@/components/HeaderApp.vue";
export default {
  name: 'App',
  created() {
    EventBus.$on('showAlert', model => {
      this.showAlert(model);
    });
  },
  data: () => ({
    alert: {
      show: false,
      color: "",
      type: null,
      msg: ""
    }
  }),
  methods: {
    showAlert(model) {
      this.alert = {
        color: model.color,
        type: model.type,
        msg: model.msg,
        show: true
      };
      setTimeout(() => this.alert.show = false, 2200);
    }
  },
  components: {
    HeaderApp
  }
};
