import "core-js/modules/es.array.push.js";
import SockJS from "sockjs-client";
import Stomp from "webstomp-client";
import { mixins } from "@/mixins";
import { EventBus } from "@/main";
import dayjs from 'dayjs';
import relativeTime from 'dayjs/plugin/relativeTime';
import ChatUsers from "@/components/chat/ChatUsers.vue";
import ChatMessages from "@/components/chat/ChatMessages.vue";
import ChatConfiguration from "@/components/chat/ChatConfiguration.vue";
import ChatInput from "@/components/chat/ChatInput.vue";
export default {
  mixins: [mixins],
  components: {
    ChatInput,
    ChatConfiguration,
    ChatUsers,
    ChatMessages
  },
  data: () => ({
    validConnection: true,
    socket: null,
    stompClient: null,
    chatMessages: [],
    chatUsers: [],
    notConnectedDialog: false,
    roomCfg: {
      hasPassword: false,
      full: false
    },
    joinDto: {
      userNickname: '',
      avatarUrl: '',
      chatRoomPassword: '',
      chatRoomId: ''
    },
    rules: {
      name: [v => !!v || 'Nickname is required', v => !(v && v.length > 14) || 'Nickname must be less than 14 characters', v => !(v && v.length < 4) || 'Nickname must be at least 4 characters'],
      password: [v => !!v || 'Password is requited']
    },
    avatarStyles: []
  }),
  created() {
    this.existsRoom();
    if (this.isSessionSetted() && !this.roomMatch()) {
      this.validConnection = false;
      EventBus.$emit('showAlert', {
        color: "error",
        type: "error",
        msg: `You cannot access this chat room.`
      });
      this.$router.push(`/404`);
    }
    if (!this.isSessionSetted()) {
      this.getAvatarStyles();
      this.validConnection = false;
      this.notConnectedDialog = true;
      this.joinDto.chatRoomId = this.$route.params.roomId;
    }
    dayjs.extend(relativeTime);
    if (this.validConnection) this.connectsWS();
  },
  methods: {
    connectsWS() {
      this.socket = new SockJS('http://localhost:8080/websocket', null, {
        sessionId: () => {
          return this.getChatId() + ":" + this.getUserId() + ":" + this.getNickname() + ":" + Math.floor(Math.random() * 99999);
        }
      });
      this.stompClient = Stomp.over(this.socket);
      //this.stompClient.debug = () => { };
      this.stompClient.connect({
        senderId: this.getUserId(),
        token: this.getAccessToken(),
        senderName: this.getNickname(),
        lobby: this.getChatId()
      }, frame => {
        this.stompClient.subscribe(`/topic/messages/${this.getChatId()}`, tick => {
          console.log(JSON.parse(tick.body));
          //this.filterLobbyInteraction(JSON.parse(tick.body));
        });
        //EventBus.$emit('reloadPlayersInLobby');
      }, error => {
        EventBus.$emit('showAlert', {
          color: "error",
          type: "error",
          msg: "Error connecting to the chat socket"
        });
      });
    },
    roomMatch() {
      return this.$route.params.roomId.toUpperCase() === this.getChatId().toUpperCase();
    },
    existsRoom() {
      this.axios.get(`/v1/cfg/check/${this.$route.params.roomId}`).then(res => {
        this.roomCfg = res.data;
        if (this.roomCfg.full) {
          EventBus.$emit('showAlert', {
            color: "info",
            type: "info",
            msg: `The room is full, try again later`
          });
        }
      }).catch(e => {
        EventBus.$emit('showAlert', {
          color: "error",
          type: "error",
          msg: `Error ${e.response.data.code}. ${e.response.data.message}`
        });
        this.$router.push(`/404`);
      });
    },
    handleRoomCfg() {},
    joinRoom() {
      this.axios.get(`/v1/room/join`).then(res => {}).catch(e => {
        EventBus.$emit('showAlert', {
          color: "error",
          type: "error",
          msg: `Error ${e.response.data.code}. ${e.response.data.message}`
        });
        this.$router.push(`/404`);
      });
    },
    getAvatarStyles() {
      this.axios.get(`/v1/cfg/avatar-styles`).then(res => {
        this.avatarStyles = res.data;
        this.newAvatarStyle('');
      });
    },
    newAvatarStyle(actualStyle) {
      const totalStyles = this.avatarStyles.length;
      const newStyle = this.avatarStyles[Math.floor(totalStyles * Math.random())];
      if (newStyle === actualStyle) this.newAvatarStyle(actualStyle);else this.joinDto.avatarUrl = newStyle;
    }
  }
};
